import { Axios } from "axios";
import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Signup.css";
import ValidationSignup from "./ValidationSignup";

const Signup = () => {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    email: "",
    phone: "",
    pass: "",
  });

//   Validation SignUPForm validationSignup.jsx
 
const [errors,setErrors]=useState({});


// ________________________________

  const handel = (e) => {
    const newData = { ...data };
    newData[e.target.name] = e.target.value;
    setData(newData);
    // console.log(newData);
  };
  const submit = (e) => {
    e.preventDefault();
    fetch("https://61d2b884b4c10c001712b552.mockapi.io/testlogin/login/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        fname: data.fname,
        lname: data.lname,
        email: data.email,
        phone: data.phone,
        pass: data.pass,
      }),
    }).then((result) => {
      result.json().then((resp) => {
        console.log(resp);
        alert("Your Regitation Sussefull");
      });
    });
  };

  return (
    <>
      <section className="vh-100 bg-image">
        <div className="mask d-flex align-items-center h-100 gradient-custom-3">
          <div className="container h-100">
            <div className="row d-flex justify-content-center align-items-center h-100">
              <div className="col-12 col-md-9 col-lg-7 col-xl-6">
                <div className="card card1">
                  <div className="card-body p-5">
                    <h2 className="text-uppercase text-center mb-5">
                      Create an account
                    </h2>

                    <form onSubmit={submit}>
                      <div className="form-outline mb-4">
                        <input
                          type="text"
                          id="form3Example1cg"
                          className="form-control form-control-lg"
                          name="fname"
                          value={data.fname}
                          onChange={(e) => {
                            handel(e);
                          }}
                          placeholder="First Name"
                        />
                      </div>

                      <div className="form-outline mb-4">
                        <input
                          type="text"
                          id="form3Example3cg"
                          className="form-control form-control-lg"
                          name="lname"
                          value={data.lname}
                          onChange={handel}
                          placeholder="Last Name"
                        />
                      </div>
                      <div className="form-outline mb-4">
                        <input
                          type="email"
                          id="form3Example3cg"
                          className="form-control form-control-lg"
                          name="email"
                          value={data.email}
                          onChange={handel}
                          placeholder="Email Address"
                        />
                      </div>

                      <div className="form-outline mb-4">
                        <input
                          type="number"
                          id="form3Example4cg"
                          className="form-control form-control-lg"
                          name="phone"
                          value={data.phone}
                          onChange={handel}
                          placeholder="Phone No"
                        />
                      </div>
                      <div className="form-outline mb-4">
                        <input
                          type="password"
                          id="form3Example4cg"
                          className="form-control form-control-lg"
                          name="pass"
                          value={data.pass}
                          onChange={handel}
                          placeholder="Password"
                        />
                      </div>

                      <div className="d-flex justify-content-center">
                        <button
                          type="submit"
                          className="btn btn-success btn-block btn-lg gradient-custom-4 text-body"
                        >
                          Register
                        </button>
                      </div>
                      <p className="text-center text-muted mt-5 mb-0">
                        Have already an account?
                        <NavLink to="/" className="fw-bold text-body">
                          <u>Login here</u>
                        </NavLink>
                      </p>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Signup;
